// app.js
import L from 'leaflet';

document.addEventListener('DOMContentLoaded', () => {
    const map = L.map('map').setView([51.505, -0.09], 13); // Coordonnées et niveau de zoom initial
    L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png').addTo(map); // Ajout d'une couche de tuiles OpenStreetMap

    // Ajoutez des marqueurs, des polygones, des événements, etc., selon vos besoins
});
